mapper.select:
select * from ( SELECT A.*, ROWNUM RUM FROM (
select

from FUN_ARCHIVE_RECORDS where 1=1 and scbj=0
AND JQBH = #{jqbh,jdbcType=VARCHAR}

    AND RECORDSCODE = #{recordscode,jdbcType=VARCHAR}


    AND ARCHIVESEQID = #{archiveseqid,jdbcType=DECIMAL}

order by id desc
) A )
WHERE rum > #{pageStart,jdbcType=DECIMAL}
AND rum <= #{pageEnd,jdbcType=DECIMAL}

SELECT COUNT(0) FROM FUN_ARCHIVE_RECORDS where 1=1 and scbj=0
AND JQBH = #{jqbh,jdbcType=VARCHAR}

    AND RECORDSCODE = #{recordscode,jdbcType=VARCHAR}


    AND ARCHIVESEQID = #{archiveseqid,jdbcType=DECIMAL}

SELECT

from FUN_ARCHIVE_RECORDS
where SCBJ=0 AND
JQBH = #{jqbh,jdbcType=VARCHAR}

    AND RECORDSCODE = #{recordscode,jdbcType=VARCHAR}


    AND ARCHIVETYPEID = #{archivetypeid,jdbcType=DECIMAL}
