mapper.select:
SELECT

FROM FUN_ARCHIVE_SFC
WHERE 1=1 AND SCBJ=0 AND ARCHIVETYPE = 0
AND CASEINFOID = #{caseinfoid,jdbcType=DECIMAL}
UNION ALL
SELECT

FROM ( SELECT A.*, ROWNUM RUM FROM (
SELECT

from FUN_ARCHIVE_SFC asfc
WHERE 1=1 AND SCBJ=0
AND ARCHIVETYPE > 0
AND CASEINFOID = #{caseinfoid,jdbcType=DECIMAL}
AND ISSUSPECTORDER = #{issuspectorder,jdbcType=DECIMAL}


    AND BASERECORDID IN (SELECT ID FROM FUN_ARCHIVE_RECORDS WHERE SCBJ=0 AND WJBM =
    UPPER(#{wjbm,jdbcType=VARCHAR})
    AND WJBID = #{wjbid,jdbcType=DECIMAL})

order by id desc
) A )
WHERE rum > #{pageStart,jdbcType=DECIMAL}
AND rum <= #{pageEnd,jdbcType=DECIMAL}

select count(0) from FUN_ARCHIVE_SFC where 1=1 and scbj=0 AND ARCHIVETYPE >= 0
and CASEINFOID = #{caseinfoid,jdbcType=DECIMAL}
AND ISSUSPECTORDER = #{issuspectorder,jdbcType=DECIMAL}

    AND BASERECORDID IN (SELECT ID FROM FUN_ARCHIVE_RECORDS WHERE SCBJ=0 AND WJBM =
    UPPER(#{wjbm,jdbcType=VARCHAR})
    AND WJBID = #{wjbid,jdbcType=DECIMAL})

mapper.update:
UPDATE FUN_ARCHIVE_SFC SET ISSUSPECTORDER=#{issuspectorder,jdbcType=DECIMAL}
,UPDATETIME = sysdate
WHERE  SCBJ=0 AND
id=#{sfcid,jdbcType=DECIMAL}

mapper.select:
SELECT
FROM FUN_ARCHIVE_SFC WHERE SCBJ=0 AND CASEINFOID = #{caseinfoid,jdbcType=DECIMAL}
AND ARCHIVETYPE = #{archivetype,jdbcType=DECIMAL}
AND ARCHIVETYPE > -1 AND ARCHIVETYPE < 2
and rownum = 1 order by id desc

SELECT COUNT(1) FROM FUN_ARCHIVE_SFC SFC
WHERE SCBJ=0 AND ARCHIVETYPE>0
AND EXISTS (SELECT 1 FROM fun_case_info info
WHERE scbj=0 AND sfc.caseinfoid=info.id

    AND CASETYPE = #{casetype,jdbcType=DECIMAL}


    AND BADWDWDM LIKE #{badwdwdm,jdbcType=VARCHAR}||'%'

)

    AND CREATETIME >= #{startTime,jdbcType=TIMESTAMP}


    AND CREATETIME <= #{endTime,jdbcType=TIMESTAMP}

mapper.update:
     UPDATE FUN_ARCHIVE_SFC
      SET ISSEND=#{issend,jdbcType=DECIMAL}
,UPDATETIME = sysdate
WHERE  SCBJ=0 AND
id=#{sfcid,jdbcType=DECIMAL}
