mapper.select:
select * from ( SELECT A.*, ROWNUM RUM FROM (
select

from FUN_ARCHIVE_SEQ aseq where 1=1 and scbj=0
and PEOPELCASEID = #{peopelcaseid,jdbcType=DECIMAL}

  AND JQBH LIKE '%'||#{jqbh,jdbcType=VARCHAR}||'%'


  AND AJBH LIKE '%'||#{ajbh,jdbcType=VARCHAR}||'%'

order by id desc
) A )
WHERE rum > #{pageStart,jdbcType=DECIMAL}
AND rum <= #{pageEnd,jdbcType=DECIMAL}

select count(0) from FUN_ARCHIVE_SEQ where 1=1 and scbj=0 AND PEOPELCASEID = #{peopelcaseid,jdbcType=DECIMAL}
AND JQBH LIKE '%'||#{jqbh,jdbcType=VARCHAR}||'%'
AND AJBH LIKE '%'||#{ajbh,jdbcType=VARCHAR}||'%'

select  from  FUN_ARCHIVE_SEQ where scbj=0
 and  archivesfcid=#{archivesfcid,jdbcType=DECIMAL}
 and  rownum=1 order by id desc
