mapper.select:
select * from ( SELECT A.*, ROWNUM RUM FROM (
select
,
(select count(0) from fun_archive_sfc aseq where pcase.jqbh=aseq.jqbh and scbj=0 ) submitcount
from FUN_CASE_PEOPLECASE pcase where 1=1 and scbj=0

    AND JQBH LIKE '%'||#{jqbh,jdbcType=VARCHAR}||'%'


    AND AJBH LIKE #{ajbh,jdbcType=VARCHAR}||'%'



    AND CASETYPE LIKE #{casetype,jdbcType=DECIMAL}

AND SYSUSERID = #{sysuserid,jdbcType=DECIMAL}
order by id desc
) A )
WHERE rum > #{pageStart,jdbcType=DECIMAL}
AND rum <= #{pageEnd,jdbcType=DECIMAL}

select count(0) from
FUN_CASE_PEOPLECASE pcase where 1=1 and scbj=0

    AND JQBH LIKE '%'||#{jqbh,jdbcType=VARCHAR}||'%'


    AND AJBH LIKE #{ajbh,jdbcType=VARCHAR}||'%'


    AND CASETYPE LIKE #{casetype,jdbcType=DECIMAL}

AND SYSUSERID = #{sysuserid,jdbcType=DECIMAL}
